text:
&lt;!-- Controls Column --&gt;
&lt;div class="col-md-4"&gt;
  &lt;h4&gt;Controls&lt;/h4&gt;

  &lt;!-- Add Player Section --&gt;
  &lt;div class="card mb-3"&gt;
    &lt;div class="card-body"&gt;
      &lt;h5 class="card-title"&gt;Add New Player&lt;/h5&gt;
      &lt;div class="mb-3"&gt;
        &lt;label for="new-player-name" class="form-label"&gt;Player Name&lt;/label&gt;
        &lt;input type="text" class="form-control" id="new-player-name" placeholder="Enter name"&gt;
      &lt;/div&gt;
      &lt;button id="add-player-btn" class="btn btn-primary"&gt;Add Player&lt;/button&gt;
    &lt;/div&gt;
  &lt;/div&gt;

  &lt;!-- Record Match Section --&gt;
  &lt;div class="card"&gt;
    &lt;div class="card-body"&gt;
      &lt;h5 class="card-title"&gt;Record a Match&lt;/h5&gt;
      &lt;div class="mb-3"&gt;
        &lt;label for="player1-select" class="form-label"&gt;Player 1&lt;/label&gt;
        &lt;select id="player1-select" class="form-select"&gt;&lt;/select&gt;
      &lt;/div&gt;
      &lt;div class="mb-3"&gt;
        &lt;label for="player2-select" class="form-label"&gt;Player 2&lt;/label&gt;
        &lt;select id="player2-select" class="form-select"&gt;&lt;/select&gt;
      &lt;/div&gt;
      &lt;div class="mb-3"&gt;
        &lt;label for="winner-select" class="form-label"&gt;Winner&lt;/label&gt;
        &lt;select id="winner-select" class="form-select"&gt;&lt;/select&gt;
      &lt;/div&gt;
      &lt;button id="record-match-btn" class="btn btn-success"&gt;Record Match&lt;/button&gt;
    &lt;/div&gt;
  &lt;/div&gt;
   &lt;div class="card mt-3"&gt;
    &lt;div class="card-body"&gt;
      &lt;h5 class="card-title"&gt;Data Management&lt;/h5&gt;
      &lt;button id="reset-data-btn" class="btn btn-danger"&gt;Reset All Data&lt;/button&gt;
    &lt;/div&gt;
  &lt;/div&gt;
&lt;/div&gt;

&lt;!-- Results Column --&gt;
&lt;div class="col-md-8"&gt;
  &lt;h4&gt;Rankings & History&lt;/h4&gt;

  &lt;!-- Rankings Table --&gt;
  &lt;h5&gt;Current Rankings&lt;/h5&gt;
  &lt;table class="table table-striped"&gt;
    &lt;thead&gt;
      &lt;tr&gt;
        &lt;th&gt;Rank&lt;/th&gt;
        &lt;th&gt;Player&lt;/th&gt;
        &lt;th&gt;Rating&lt;/th&gt;
        &lt;th&gt;Wins&lt;/th&gt;
        &lt;th&gt;Losses&lt;/th&gt;
      &lt;/tr&gt;
    &lt;/thead&gt;
    &lt;tbody id="rankings-body"&gt;
    &lt;/tbody&gt;
  &lt;/table&gt;

  &lt;!-- Rating History Chart --&gt;
  &lt;h5 class="mt-4"&gt;Rating History&lt;/h5&gt;
  &lt;div&gt;
    &lt;canvas id="ratings-chart"&gt;&lt;/canvas&gt;
  &lt;/div&gt;

  &lt;h5 class="mt-4"&gt;Match History&lt;/h5&gt;
  &lt;table class="table table-sm"&gt;
    &lt;thead&gt;
      &lt;tr&gt;
        &lt;th&gt;Player 1&lt;/th&gt;
        &lt;th&gt;Player 2&lt;/th&gt;
        &lt;th&gt;Winner&lt;/th&gt;
      &lt;/tr&gt;
    &lt;/thead&gt;
    &lt;tbody id="match-history-body"&gt;
    &lt;/tbody&gt;
  &lt;/table&gt;
&lt;/div&gt;
